css:
/* Reset some default styles */
body, h1, h2, h3, p, ul, li, table, th, td {
    margin: 0;
    padding: 0;
    color: black;
}

body {
    font-family: Arial, sans-serif;
    margin: 20px;
}
.header {
    margin-top: -10px;
}
.date {
    font-size: 13px;
}
.info-d {
    font-size: 16px;
    font-weight: bold;
}
.title {
    margin-top: 0px;
}
.prepared_by {
    margin-top: 20px;
}
.represented_by {
    margin-top: 20px;
}
.created_for {
    margin-top: 20px;
}
.candidate {
    margin-top: 10px;
}
.candidate-detail {
    margin-top: 10px;
}
.whereas {
    margin-top: 10px;
}
.where-d {
    margin-top: 10px;
}
.therefore {
    margin-top: 20px;
}
.terms {
    margin-top: 10px;
}
.witness {
    margin-top: 20px;
}

ul {
    margin-top: 0px;
    margin-bottom: 0px;
    list-style: disc;
    padding-left: 45px;
    font-size: 13px;
}
.sign-div {
  width: 100%;
  margin-top: 20px;
}
.candidate-sign-div {
    width: 100%;
    margin-top: 140px;
}
.leftblock {
  font-size: 12px;
  float: left;
  width: 10%;
}

.rightblock {
  float: left;
  width: 90%;
  font-weight: bold;
  font-size: 14px;
  margin-top: 10px;
}

.print-format .sign-div.col-xs-12 img {
    width: 16% !important;
    padding-top: 5px;
    padding-left: 5px;
}
.sign-div .col-xs-12 {
    padding-left: 0px;
    padding-right: 0px;
}
.candidate-sign-div .col-xs-12 {
    padding-left: 0px;
    padding-right: 0px;
}
.footer {
    position: fixed;
    bottom: 0;
    padding-bottom: 0px;
    left: 0;
    right: 0;
}
.footer-space {
    height: 80px;
}

@media print {
    table td div {
        page-break-inside: auto !important;
    }
    
    table {
        page-break-after: auto !important;
    }

    tr {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    td {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    th {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    #thead {
        display: table-header-group;

    }

    tfoot {
        display: table-footer-group;
    }
    .header {

        top: 0;
    }
    .footer-img {

        bottom: 0;
    }
}
