css:
.sign-div {
  width: 100%;
  margin-top: -15px;
}
.print-format .leftblock img {
    width: 27% !important;
    padding-top: 10px;
    padding-left: 5px;
}
.sign-div .col-xs-12 {
    padding-left: 0px;
    padding-right: 0px;
}
.date_en{
    font-family: 'Calibri';
    color: black;
    font-size:14px;
    text-align: justify;
    line-height: 1.3 !important;
}
.descrip_ar{
    font-family: 'Calibri';
    color: black;
    font-size:15px;
    text-align: justify;
    text-align-last: right;
    line-height: 1.3 !important;
}
.name_en{
    font-family: 'Calibri';
    color: black;
    font-size:17px;
    font-weight: bold;
}
.name_ar{
    font-family: 'Calibri';
    color: black;
    font-size:16px;
    font-weight: bold;
}
.salary-table {
    width: 95%;
    border-collapse: collapse;
    margin-top: 5px;
    margin-bottom: 2px;
    margin-left: 14px !important;
    font-family: 'Calibri';
    color: black;
}
.terms span {
    font-family: 'Calibri';
    font-size: 14px !important;
    color: black;
    line-height: 1.8;
}
.terms ol {
    line-height: 4mm;
}
.terms div {
    text-align: justify;
}
.terms .ql-editor ol {
    padding-left: 0em;
}
.ql-editor.read-mode {
    padding: 2px !important;
}
.terms-ar span {
    font-family: 'Calibri';
    font-size: 15px !important;
    color: black;
    line-height: 1.8;
}
.terms-ar ol {
    line-height: 4mm;
}
.terms-ar .ql-editor ol {
    padding-left: 0em;
}
.terms-ar .ql-editor li {
    padding-left: 0em;
}
.terms-ar li {
    text-align: justify !important; 
}
.terms-ar p {
    text-align: justify !important; 
}
.leftblock {
  font-size: 12px;
  float: left;
  width: 50%;
  font-weight: bold;
}
.rightblock {
  float: left;
  width: 50%;
  font-weight: bold;
  font-size: 12px;
}
.note-div p{
    font-size:11px;
    margin: 0px 0px 0px;
}
.salary-table th, .salary-table td {
    border: 1px solid #000;
    padding: 2px !important;
    font-size: 13px;
}
.salary-table th {
    background-color: #179CD7 !important;
    color: #FFFFFF !important;
}
.footer {
    position: fixed;
    bottom: 0;
    padding-bottom: 0px;
    left: 0;
    right: 0;
    width: 100%;
}
.col-xs-12 {
    padding-left: 0px;
    padding-right: 0px;
}
.print-format td {
    padding: 1px !important;
}
.footer-space {
    height: 80px;
}
body:last-child .print-format .leftblock img {
    width: 27% !important;
    padding-top: 10px;
    padding-left: 5px;
}
@media print {
    table td div {
        page-break-inside: auto !important;
    }
    table {
        page-break-after: auto !important;
    }

    tr {
        page-break-inside: auto;
        page-break-after: auto;
    }
    td {
        page-break-inside: auto;
        page-break-after: auto;
    }

    th {
        page-break-inside: avoid;
        page-break-after: auto;
    }
    #thead {
        display: table-header-group;

    }
    tfoot {
        display: table-footer-group;
    }
    .header {
        top: 0;
    }
    .footer-img {
        bottom: 0;
    }
}
