css:
/* Reset some default styles */
body, h1, h2, h3, p, ul, li, table, th, td {
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    margin: 20px;
}
.header {
    margin-top: -10px;
}
.date {
    font-size: 14px;
}
.info-d {
    font-size: 18px;
    font-weight: bold;
}
.salary-table {
    width: 90%;
    border-collapse: collapse;
    margin-top: 10px;
    margin-left: 25px !important;
    margin-bottom: 10px;
}

.salary-table th, .salary-table td {
    border: 1px solid #000;
    padding: 2px !important;
    font-size: 15px;
}

.salary-table th {
    background-color: #12A8CE !important;
    color: #FFFFFF !important;
}

.terms p {
    margin: 0;
    padding: 0;
    font-size: 14px;
}

ul {
    margin-top: 10px;
    list-style: disc;
    padding-left: 45px;
    font-size: 14px;
}
.sign-div {
  width: 100%;
  margin-top: 12px;
}
.leftblock {
  font-size: 14px;
  float: left;
  width: 50%;
}

.rightblock {
  float: left;
  width: 50%;
  font-weight: bold;
  font-size: 14px;
}

.print-format .sign-div.col-xs-12 img {
    width: 16% !important;
    padding-top: 5px;
    padding-left: 5px;
}
.sign-div .col-xs-12 {
    padding-left: 0px;
    padding-right: 0px;
}
.note-div p{
    font-size:14px;
}

.footer {
    position: fixed;
    bottom: 0;
    padding-bottom: 0px;
    left: 0;
    right: 0;
}
.footer-space {
    height: 80px;
}

@media print {
    table td div {
        page-break-inside: auto !important;
    }
    
    table {
        page-break-after: auto !important;
    }

    tr {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    td {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    th {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    #thead {
        display: table-header-group;

    }

    tfoot {
        display: table-footer-group;
    }
    .header {

        top: 0;
    }
    .footer-img {

        bottom: 0;
    }
}
